
public class VariableDemo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=3;
		int b=7;
		
		System.out.println("Nilai a : "+ a+" Nilai b : " + b);
		
		int temp = a;
		a=b;
		b=temp;
		
		System.out.println("Nilai a : "+ a+" Nilai b : " + b);

		//----------------------------------------------------
		boolean c=true;
		boolean d=false;
		
		
		//System.out.println(c+d);
		System.out.println("Nilai c : "+ c+" Nilai d : " + d);
		
	}
		
	
}
